/**
 * Matcht integer mit Strings
 * bsp: 2 == wide
 */
function getCorrespondingSizeToInt(int) {
    sizeClass = "";
    if (int == 1) {
        sizeClass = "small";
    } else if (int == 2) {
        sizeClass = "wide";
    } else if (int == 3) {
        sizeClass = "big";
    }
    return sizeClass;
}

function getWidgetSize(size) {
    widget = [];
    widget_size_x = 1;
    widget_size_y = 1;
    if (size == 3) {
        widget_size_x = 2;
        widget_size_y = 2;
    } else if (size == 2) {
        widget_size_x = 2;
        widget_size_y = 1;
    }
    widget.x = widget_size_x;
    widget.y = widget_size_y;
    return widget;
}
